#!/bin/bash
set -e

echo "🚀 DMG Holdings Database Backend - Coolify Deployment"
echo "======================================================"
echo ""

# Colors for output
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
RED='\033[0;31m'
NC='\033[0m' # No Color

# Check if we're in the right directory
if [ ! -d "server" ]; then
    echo -e "${RED}❌ Error: server/ directory not found${NC}"
    echo "Please run this script from the repository root"
    exit 1
fi

echo -e "${YELLOW}📋 Deployment Checklist:${NC}"
echo ""
echo "This script will help you deploy the database backend in Coolify."
echo "You'll need to:"
echo "  1. Deploy PostgreSQL database in Coolify dashboard"
echo "  2. Deploy API server in Coolify dashboard"
echo "  3. Update frontend environment variables"
echo ""
read -p "Press Enter to continue or Ctrl+C to cancel..."

echo ""
echo -e "${GREEN}✅ Repository is ready!${NC}"
echo ""
echo "📝 Next Steps:"
